define("Core/helpers/Hcontrol/getScrollWidth",function(){var r=function(){if(document&&document.body){var e=document.createElement("div");e.style.cssText="position:absolute;height:50px;overflow-y:scroll;visibility:hidden",e.innerHTML='<div style="height:100px"></div>',document.body.appendChild(e);var t=e.offsetWidth-e.clientWidth;return document.body.removeChild(e),r=function(){return t},t}else if(!document)throw new Error(rk("Ошибка: функция $ws.helpers.getScrollWidth вызвана на сервере. Она должна вызываться только в клиентском браузере"));else throw new Error(rk("Ошибка: функция $ws.helpers.getScrollWidth вызвана на клиентском браузере, однако body ещё не готово"))};return r});