
import { Channel as EventBusChannel } from 'Env/Event';
/**
 * Транспорт передачи сообщений между вкладками
 */
export interface Transport {
    /**
     * Отправка сообщения
     * @param {string} message Имя сообщения
     * @param {*} data Отправляемые данные
     */
    notify(message: string, data: any): void;
    destroy(): void;
}

export interface TransportConstructor {
    /**
     *
     * @param {Core/EventBusChannel} channel Канал, в который будут отправлены сообщения от других вкладок
     * @constructor
     */
    // @ts-ignore
    new(channel: EventBusChannel): Transport;
}
