// @ts-ignore
import { FetchResponseType } from "Browser/_Transport/fetch/responseParser";
import { FetchConfig, AbortPromise } from "Browser/_Transport/fetch/interface";

/**
 * Транспорт-обёртка над fetch, отвечающая за разбор ответа от сервера.
 * Так же позволяющая предотвратить запрос
 * @param {Env/Transport/FetchConfig} requestConfig
 * @param {Env/Transport/fetch/ResponseType} responseType
 * @return {Env/Transport/AbortPromise}
 *
 * @function
 * @name Env/Transport/SbisTransport
 * @author Заляев А.В.
 * @public
 */
export type SbisTransport = <T = string>(
    requestConfig: Partial<FetchConfig>,
    responseType: FetchResponseType
) => AbortPromise<T>;
