/// <amd-module name="SbisEnv/_Error/IController" />
import { Handler, HandlerConfig } from 'SbisEnv/_Error/Handler';
// @ts-ignore
import * as Control from 'Core/Control';
/**
 * Интерфейс компонента, отвечающий за обработку ошибок
 * @interface IErrorController
 */
export default interface IErrorController extends Control {
    /**
     * Вызов обработки ошибки
     * Осуществляется поочерёдным вызовом зарегестрированных обработчиков ошибок, до тех пор,
     * пока не будет получен результат.
     * Если на текущем уровне компонента не найдётся подходящего обработчика,
     * то компонент будет брать список обработчиков, зарегестрированных на уровне всего приложения
     *
     */
    errorHandling<T extends Error = Error>(config: T | HandlerConfig<T>)
    addHandler(handler: Handler): void;
    removeHandler(handler: Handler);
}
