/// <amd-module name="SbisEnv/_Maintains/Parking/_action/AskAdmin" />
// @ts-ignore
import * as Control from 'Core/Control';
import * as _template from 'wml!SbisEnv/_Maintains/Parking/_action/AskAdmin';
import { ACTION, DETAILS } from "SbisEnv/_Maintains/Parking/Const";

class AskAdmin extends Control {
    _template = _template;
    title = rk(DETAILS.ASK_ADMIN);
    isAsked = false;
    caption = rk(ACTION.ASK_ADMIN);
    action() {
        if (typeof window === 'undefined') {
            return;
        }
        // @ts-ignore
        window.location = '/';
        this.isAsked = true;
    }
}

export default AskAdmin;
