/// <amd-module name="SbisEnv/_Maintains/Parking/_action/Reload" />
// @ts-ignore
import * as Control from 'Core/Control';
import * as _template from 'wml!SbisEnv/_Maintains/Parking/_action/link';
import { ACTION } from "SbisEnv/_Maintains/Parking/Const";

class Reload extends Control {
    _template = _template;
    caption = rk(ACTION.RELOAD);
    action() {
        if (typeof window === 'undefined') {
            return;
        }
        setTimeout(() => {
            window.location.reload();
        })
    }
}

export default Reload;
