/// <amd-module name="SbisEnv/_Maintains/Parking/_handlers/getDefOptions" />
import { Mode } from 'SbisEnv/Error';
import { Size } from 'SbisEnv/_Maintains/Parking/Const';

let getSize = (mode: Mode) => {
    switch (mode) {
        case Mode.page: {
            return Size.large;
        }
        case Mode.dialog: {
            return Size.medium;
        }
        default: {
            return Size.large;
        }
    }
};

let getDefOptions = (error: Error, mode?: Mode) => {
    let size = getSize(mode);
    
    return {
        size
    }
};

export default getDefOptions;
