/// <amd-module name="SbisFile/Source/_Const" />
export const KB = 1024;
export const MB = KB * KB;
export const GB = MB * KB;

/**
 * Максимальный размер для отправки пакета в МБ
 */
export const MAX_MB_FOR_SEND = 100;

/**
 * Максимальный размер для отправки целостного файла в КБ
 */
export const MAX_SIZE_FOR_SEND = MAX_MB_FOR_SEND * MB;
export const PART_SIZE = 10 * MB; // размер загружаемого куска файла
export const UPLOAD_DIR_ERROR = '';
export const MAX_UPLOAD_SIZE_ERROR = rk('Превышен допустимый размер загружаемого файла');
