define('Deprecated/TransportAdapterGETJSON', [
   'Deprecated/TransportAdapterAbstract',
   'Env/Env'
], function(TransportAdapterAbstract, Env) {
   var TransportAdapterGETJSON;

   /**
    * Класс-адаптер для вызова методов через GET
    *
    * @class Deprecated/TransportAdapterGETJSON
    * @extends Deprecated/TransportAdapterAbstract
    * @author Бегунов А.В.
    * @public
    * @deprecated
    */
   TransportAdapterGETJSON = TransportAdapterAbstract.extend(/** @lends Deprecated/TransportAdapterGETJSON.prototype */{
      $protected: {
         _options: {
            url: ''
         },
         _xhrTransport: null
      },
      list: function(args) {
         this._xhrTransport =
            Env.IoC.resolve('ITransport', {
               'url': this._options.url
            });
         var
            query = [];
         for (var i in args) {
            query.push(encodeURI(i) + "=" + encodeURI(args[i]))
         }
         return this._xhrTransport.execute(query.join("&"));
      },
      getRPCClient: function() {
         return {};
      },
      abort: function() {
         if (this._xhrTransport) {
            this._xhrTransport.abort();
         }
      }
   });

   return TransportAdapterGETJSON;
});