define('Deprecated/TransportAdapterRPCJSON', [
   'Deprecated/TransportAdapterRPCAbstract',
   'Browser/Transport'
], function(TransportAdapterRPCAbstract, Transport) {
   var TransportAdapterRPCJSON;

   /**
    * Класс-адаптер для работы с JSON-RPC службой
    *
    * @class Deprecated/TransportAdapterRPCJSON
    * @extends Deprecated/TransportAdapterRPCAbstract
    * @author Бегунов А.В.
    * @public
    * @deprecated
    */
   TransportAdapterRPCJSON = TransportAdapterRPCAbstract.extend(/** @lends Deprecated/TransportAdapterRPCJSON.prototype */{
      _createRpcClient: function() {
         return new Transport.RPCJSON({
            serviceUrl: this._options.serviceUrl
         });
      }
   });

   return TransportAdapterRPCJSON;
});