#!/usr/bin/env node
let fs = require('fs');
const logger = console;
const map = {
   'node_modules/sbis3-ws/WS.Core': 'WS.Core',
   'node_modules/saby-types/Types': 'Types',
   'node_modules/sbis3-ws/View': 'View',
   'node_modules/sbis3-ws/Inferno': 'Inferno'
};

Object.keys(map).forEach((source) => {
   let target = map[source];
   logger.warn(`Create symlink from '${source}' to '${target}'`);

   try {
      fs.symlinkSync(source, target, 'dir');
   } catch (err) {
      logger.warn(err.message);
   }
});
